#!/bin/bash# First parameter $1 - archive name# Second parameter $2 - list of dirs to be backed upRUNDATE=`date +%x` &&RUNDATE=`./cutslash.sh $RUNDATE` &&echo $RUNDATE &&FILENAME="/backup/backup_"$RUNDATE"_"$1".tar" &&DIR="" &&tar -cvf $FILENAME /backup/scriptnconf && touch $FILENAME &&for i in `cat $2`do    DIR="/"$i &&    echo $DIR &&    tar -rvf $FILENAME $DIR doneecho ""gzip $FILENAME &&
